using ImgMzx;
using System.Diagnostics;
using System.Runtime.InteropServices;

namespace ImgMzxTests;

[TestClass]
public class AppImagesTest
{
    [TestMethod]
    public void LoadsAllVectors()
    {
        /*
UPDATE images
SET vector = X''
WHERE LENGTH(vector) = 0; 
        */

        using var images = new Images(
            $@"{AppContext.BaseDirectory}db\spacer.db", 
            AppConsts.FileVit, 
            AppConsts.FileMask);
        var progressMessages = new List<string>();
        var progress = new Progress<string>(msg => progressMessages.Add(msg));

        var sw = Stopwatch.StartNew();
        images.Load(progress);
        sw.Stop();

        Console.WriteLine($"Load time: {sw.ElapsedMilliseconds} ms");
        foreach (var msg in progressMessages)
            Console.WriteLine(msg);

        var count = images.GetCount();
        Assert.IsGreaterThan(0, count, "No vectors loaded");
    }

    [TestMethod]
    public void CalcualteAllVectors()
    {
        using var images = new Images(
            $@"{AppContext.BaseDirectory}db\spacer.db",
            AppConsts.FileVit,
            AppConsts.FileMask);
        var progressMessages = new List<string>();
        var progress = new Progress<string>(msg => progressMessages.Add(msg));
        images.Load(progress);

        var hashes = images.GetAllHashes().ToArray();
        int total = hashes.Length;
        int updated = 0;
        var sw = Stopwatch.StartNew();

        for (int i = 0; i < total; i++) {
            var hash = hashes[i];
            var vector = images.GetVector(hash);
            if (vector.Length == 0 || float.IsNaN(vector[0])) {
                var imgData = AppFile.ReadMex(hash);
                if (imgData != null) {
                    using var image = AppBitmap.GetImage(imgData);
                    if (image != null) {
                        var newVector = images.Vit.CalculateVector(image);
                        images.UpdateVector(hash, newVector);
                        images.UpdateImgInDatabase(hash, AppConsts.AttributeVector, MemoryMarshal.AsBytes(newVector.AsSpan()).ToArray());
                        updated++;
                    }
                }
            }
            if ((i + 1) % 100 == 0 || i == total - 1) {
                Debug.WriteLine($"Progress: {i + 1}/{total}, updated: {updated}");
            }
        }
        sw.Stop();
        Debug.WriteLine($"Done. Updated {updated} vectors in {sw.Elapsed.TotalSeconds:F1} s.");
    }
}